// Remove the attribute Style from the element.
func (e *SVGAnimateTransformElement) StyleRemove(keys ...string) *SVGAnimateTransformElement {
	if e.keyValueStrings == nil {
		return e
	}
	kv, ok := e.keyValueStrings.Get("style")
	if !ok {
		return e
	}
	kv.Remove(keys...)
	return e
}
